import React, { useState } from 'react';

function Planner() {
    const [isOpen, setIsOpen] = useState(false);
    const [goal, setGoal] = useState('');
    const [priority, setPriority] = useState('');
    const [startTime, setStartTime] = useState('');
    const [endTime, setEndTime] = useState('');
    const [status, setStatus] = useState('Not Started');
    const [goalsList, setGoalsList] = useState([]);
    const [breakTimes, setBreakTimes] = useState('');
    const [selectedDays, setSelectedDays] = useState([]);

    const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

    const toggleDay = (day) => {
        setSelectedDays((prev) => 
            prev.includes(day) ? prev.filter((d) => d !== day) : [...prev, day]
        );
    };

    const togglePopup = () => {
        setIsOpen(!isOpen);
    };

    const handleGoalChange = (event) => {
        setGoal(event.target.value);
    };

    const handlePriorityChange = (event) => {
        setPriority(event.target.value);
    };

    const handleStartTimeChange = (event) => {
        setStartTime(event.target.value);
    };

    const handleEndTimeChange = (event) => {
        setEndTime(event.target.value);
    };

    const handleStatusChange = (event, index) => {
        const newStatus = event.target.value;
        const updatedGoals = [...goalsList];
        updatedGoals[index].status = newStatus;
        setGoalsList(updatedGoals);
    };

    const handleBreakTimes = (event) => {
        setBreakTimes(event.target.value);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        if (goal && priority && startTime && endTime && status && breakTimes && selectedDays.length) {
            const newGoal = {
                goal: goal,
                priority: priority,
                startTime: startTime,
                endTime: endTime,
                status: status,
                breakTimes: breakTimes,
                days: selectedDays.join(', ')
            };
            setGoalsList([...goalsList, newGoal]);
            setGoal('');
            setPriority('');
            setStartTime('');
            setEndTime('');
            setStatus('Not Started');
            setBreakTimes('');
            setSelectedDays([]);
            setIsOpen(false);
        }
    };

    return (
        <div className="planner">
            <section>
                <h2>Welcome to Planner</h2>
                <h3>Where you can write your path down for Success</h3>
            </section>
            <section>
                <h2>Planner Table</h2>
                <div className="goalenterer">
                    <button onClick={togglePopup}>Enter Goal</button>
                    {isOpen && (
                        <div className="popup-overlay">
                            <div className="popup-content">
                                <form id="myform" onSubmit={handleSubmit}>
                                    <label htmlFor="goal">What is your goal?</label>
                                    <input
                                        type="text"
                                        id="goal"
                                        name="goal"
                                        value={goal}
                                        onChange={handleGoalChange}
                                    /><br />
                                    <label htmlFor="priority">What Priority is your Goal?</label>
                                    <input
                                        type="text"
                                        id="priority"
                                        name="priority"
                                        value={priority}
                                        onChange={handlePriorityChange}
                                    /><br />
                                    <label htmlFor="start-time">Start Time and Date:</label>
                                    <input
                                        type="datetime-local"
                                        id="start-time"
                                        name="start-time"
                                        value={startTime}
                                        onChange={handleStartTimeChange}
                                    /><br />
                                    <label htmlFor="end-time">End Time and Date:</label>
                                    <input
                                        type="datetime-local"
                                        id="end-time"
                                        name="end-time"
                                        value={endTime}
                                        onChange={handleEndTimeChange}
                                    /><br />
                                    <label>Select Days:</label>
                                    <div className="day-selector">
                                        {daysOfWeek.map((day) => (
                                            <button
                                                type="button"
                                                key={day}
                                                className={selectedDays.includes(day) ? 'selected' : ''}
                                                onClick={() => toggleDay(day)}
                                            >
                                                {day}
                                            </button>
                                        ))}
                                    </div>
                                    <br />
                                    <label htmlFor="status">Status:</label>
                                    <select
                                        id="status"
                                        name="status"
                                        value={status}
                                        onChange={(e) => setStatus(e.target.value)}
                                    >
                                        <option value="Not Started">Not Started</option>
                                        <option value="In Progress">In Progress</option>
                                        <option value="Completed">Completed</option>
                                        <option value="Paused">Paused</option>
                                    </select><br />
                                    <label htmlFor="break-times">Break Times:</label>
                                    <input 
                                        type="datetime-local"
                                        id="break-times"
                                        name="break-times"
                                        value={breakTimes}
                                        onChange={handleBreakTimes}
                                    /><br/>
                                    <input type="submit" id="gsubmit" value="Submit" />
                                </form>
                                <button onClick={togglePopup}>Close</button>
                            </div>
                        </div>
                    )}
                </div>
                <table className="use" cellPadding="5">
                    <thead>
                        <tr>
                            <th scope="col">Priority</th>
                            <th scope="col">Goals</th>
                            <th scope="col">Start Time</th>
                            <th scope="col">End Time</th>
                            <th scope="col">Days</th>
                            <th scope="col">Status</th>
                            <th scope="col">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {goalsList.map((goalItem, index) => (
                            <React.Fragment key={index}>
                                <tr>
                                    <td>{goalItem.priority}</td>
                                    <td>{goalItem.goal}</td>
                                    <td>{goalItem.startTime}</td>
                                    <td>{goalItem.endTime}</td>
                                    <td>{goalItem.days}</td>
                                    <td>
                                        <select
                                            value={goalItem.status}
                                            onChange={(e) => handleStatusChange(e, index)}
                                        >
                                            <option value="Not Started">Not Started</option>
                                            <option value="In Progress">In Progress</option>
                                            <option value="Completed">Completed</option>
                                            <option value="Paused">Paused</option>
                                        </select>
                                    </td>
                                    <td><button>Complete</button></td>
                                </tr>
                                <tr>
                                    <td colSpan="7">Break Times: {goalItem.breakTimes}</td>
                                </tr>
                            </React.Fragment>
                        ))}
                    </tbody>
                </table>
            </section>
        </div>
    );
}

export default Planner;
