import React, { useState } from 'react';
import { Link, useOutletContext } from 'react-router-dom';
import './Planner.css';

function Planner() {
    const { activities } = useOutletContext();
    const [isOpen, setIsOpen] = useState(false);
    const [vision, setVision] = useState('');
    const [plan, setPlan] = useState('');
    const [organization, setOrganization] = useState('');
    const [prepare, setPrepare] = useState('');
    const [progress, setProgress] = useState('Not Started');
    const [goalsList, setGoalsList] = useState([]);
    const [schedule, setSchedule] = useState({}); // Added schedule state

    const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

    const togglePopup = () => setIsOpen(!isOpen);

    const handleScheduleChange = (day, field, value) => {
        setSchedule((prev) => ({
            ...prev,
            [day]: { ...prev[day], [field]: value },
        }));
    };

    const addBreakTime = (day) => {
        setSchedule((prev) => {
            const breaks = prev[day]?.breaks || [];
            return {
                ...prev,
                [day]: {
                    ...prev[day],
                    breaks: [...breaks, { start: '', end: '' }],
                },
            };
        });
    };

    const updateBreakTime = (day, index, field, value) => {
        setSchedule((prev) => {
            const breaks = prev[day]?.breaks.map((breakTime, i) => (
                i === index ? { ...breakTime, [field]: value } : breakTime
            ));
            return {
                ...prev,
                [day]: { ...prev[day], breaks },
            };
        });
    };

    const removeBreakTime = (day, index) => {
        setSchedule((prev) => {
            const breaks = prev[day]?.breaks.filter((_, i) => i !== index);
            return {
                ...prev,
                [day]: { ...prev[day], breaks },
            };
        });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        if (vision && plan && organization && prepare && progress && Object.keys(schedule).length) {
            const newGoal = {
                vision,
                plan,
                organization,
                prepare,
                schedule,
                progress,
            };
            setGoalsList([...goalsList, newGoal]);
            setVision('');
            setPlan('');
            setOrganization('');
            setPrepare('');
            setSchedule({});
            setProgress('Not Started');
            setIsOpen(false);
        }
    };

    return (
        <div className="planner">
            <section>
                <h2>Welcome to Planner</h2>
                <h3>Where you can write your path down for Success</h3>
            </section>
            <section>
                <h2>Planner Table</h2>
                <div className="goalenterer">
                    <button onClick={togglePopup}>Enter Goal</button>
                    {isOpen && (
                        <div className="popup-overlay">
                            <div className="popup-content">
                                <form id="myform" onSubmit={handleSubmit} className="form-grid">
                                    <div>
                                        <label htmlFor="vision">Vision:</label>
                                        <input
                                            type="text"
                                            id="vision"
                                            value={vision}
                                            onChange={(e) => setVision(e.target.value)}
                                        />
                                        <Link to="/activitysetter">
                                            <button type="button" className="planner-button">
                                                Go to Activity Setter
                                            </button>
                                        </Link>
                                    </div>
                                    <div>
                                        <label htmlFor="plan">Plan:</label>
                                        <input
                                            type="text"
                                            id="plan"
                                            value={plan}
                                            onChange={(e) => setPlan(e.target.value)}
                                        />
                                    </div>
                                    <div>
                                        <label htmlFor="organization">Organization:</label>
                                        <input
                                            type="text"
                                            id="organization"
                                            value={organization}
                                            onChange={(e) => setOrganization(e.target.value)}
                                        />
                                    </div>
                                    <div>
                                        <label htmlFor="prepare">Prepare:</label>
                                        <input
                                            type="text"
                                            id="prepare"
                                            value={prepare}
                                            onChange={(e) => setPrepare(e.target.value)}
                                        />
                                    </div>
                                    <div className="schedule-section">
                                        <h3>Work Schedule:</h3>
                                        {daysOfWeek.map((day) => (
                                            <div key={day} className="day-schedule">
                                                <label>
                                                    <input
                                                        type="checkbox"
                                                        checked={!!schedule[day]?.active}
                                                        onChange={(e) => handleScheduleChange(day, 'active', e.target.checked)}
                                                    />
                                                    {day}
                                                </label>
                                                {schedule[day]?.active && (
                                                    <div className="time-inputs">
                                                        <input
                                                            type="time"
                                                            placeholder="Start Time"
                                                            value={schedule[day]?.startTime || ''}
                                                            onChange={(e) => handleScheduleChange(day, 'startTime', e.target.value)}
                                                        />
                                                        <input
                                                            type="time"
                                                            placeholder="End Time"
                                                            value={schedule[day]?.endTime || ''}
                                                            onChange={(e) => handleScheduleChange(day, 'endTime', e.target.value)}
                                                        />
                                                        <div>
                                                            <button type="button" onClick={() => addBreakTime(day)}>
                                                                + Add Break
                                                            </button>
                                                        </div>
                                                        {schedule[day]?.breaks?.map((breakTime, index) => (
                                                            <div key={index}>
                                                                <input
                                                                    type="time"
                                                                    placeholder="Break Start"
                                                                    value={breakTime.start}
                                                                    onChange={(e) => updateBreakTime(day, index, 'start', e.target.value)}
                                                                />
                                                                <input
                                                                    type="time"
                                                                    placeholder="Break End"
                                                                    value={breakTime.end}
                                                                    onChange={(e) => updateBreakTime(day, index, 'end', e.target.value)}
                                                                />
                                                                <button type="button" onClick={() => removeBreakTime(day, index)}>
                                                                    Remove Break
                                                                </button>
                                                            </div>
                                                        ))}
                                                    </div>
                                                )}
                                            </div>
                                        ))}
                                    </div>
                                    <div className="form-actions">
                                        <input type="submit" id="gsubmit" value="Submit" />
                                        <button onClick={togglePopup}>Close</button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    )}
                </div>
                <table className="use" cellPadding="5">
                    <thead>
                        <tr>
                            <th>Vision</th>
                            <th>Plan</th>
                            <th>Organization</th>
                            <th>Prepare</th>
                            <th>Progress</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {goalsList.map((goalItem, index) => (
                            <tr key={index}>
                                <td>{goalItem.vision}</td>
                                <td>{goalItem.plan}</td>
                                <td>{goalItem.organization}</td>
                                <td>{goalItem.prepare}</td>
                                <td>{goalItem.progress}</td>
                                <td>
                                    <Link to="/activitysetter" className="action-link">Set Activity</Link>
                                    <ul>
                                        {activities.map((activity, activityIndex) => (
                                            <li key={activityIndex}>{activity}</li>
                                        ))}
                                    </ul>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </section>
        </div>
    );
}

export default Planner;
