import React, { useState } from 'react';
import './Planner.css';

// The GoalSetter component for entering a goal
function ActivitySetter({ onGoalSubmit }) {
  const [goal, setGoal] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (goal) {
      onGoalSubmit(goal);
      setGoal(''); // Reset goal after submitting
    }
  };

  return (
    <form className="goal-form" onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Enter your goal"
        value={goal}
        onChange={(e) => setGoal(e.target.value)}
        required
      />
      <button type="submit" className="goal-submit-button">Set Goal</button>
    </form>
  );
}

// The Planner component
function Planner() {
  const [goal, setGoal] = useState(''); // State for the goal
  const [activities, setActivities] = useState([]); // State for activities

  // Handle submission of the goal
  const handleGoalSubmission = (newGoal) => {
    setGoal(newGoal); // Set the new goal
  };

  // Handle adding new activities
  const addActivity = (activity) => {
    setActivities([...activities, activity]); // Add activity to list
  };

  const deleteActivity = (index) => {
    const updatedActivities = activities.filter((_, i) => i !== index);
    setActivities(updatedActivities); // Remove activity by index
  };

  return (
    <div className="planner-container">
      <h1 className="planner-title">Planner</h1>

      {/* Goal Section */}
      <section className="goal-section">
        <h2>Enter Your Goal</h2>
        <ActivitySetter onGoalSubmit={handleGoalSubmission} />
        <h3>Your Current Goal: {goal || 'No goal set'}</h3>
      </section>

      {/* Activities Table */}
      <section className="planner-table-section">
        <h2>Activity Table</h2>
        <table className="activity-table">
          <thead>
            <tr>
              <th>Activity</th>
              <th>Status</th>
              <th>Priority</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {activities.map((activity, index) => (
              <tr key={index}>
                <td>{activity.name}</td>
                <td>{activity.status}</td>
                <td>{activity.priority}</td>
                <td>
                  <button className="edit-button">Edit</button>
                  <button className="delete-button" onClick={() => deleteActivity(index)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Add Activity Button */}
        <button className="add-activity-button" onClick={() => addActivity({
          name: 'New Activity',
          status: 'Incomplete',
          priority: activities.length + 1,
        })}>
          Add Activity
        </button>
      </section>
    </div>
  );
}

export default Planner;
