import React, { useRef, useEffect } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls.js';
import { GUI } from 'dat.gui';

function Planner() {
    const mountRef = useRef(null);

    useEffect(() => {
        const mountNode = mountRef.current; // Store the initial ref value
        if (!mountNode) return;

        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 1, 10000);
        camera.position.set(0, 20, 100);
        
        const renderer = new THREE.WebGLRenderer();
        renderer.setClearColor(0x0ebee6);
        renderer.setSize(window.innerWidth, window.innerHeight);
        renderer.setPixelRatio(window.devicePixelRatio);
        renderer.shadowMap.enabled = true;
        renderer.shadowMap.type = THREE.PCFSoftShadowMap;
        mountNode.appendChild(renderer.domElement);

        const controls = new OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.1;
        controls.enableZoom = true;
        
        // Ground plane
        const geometren = new THREE.PlaneGeometry(15000, 15000, 100, 100);
        geometren.rotateX(-Math.PI / 2);
        const material2 = new THREE.MeshStandardMaterial({
            color: 0x00aaff,
            roughness: 0.6,
            metalness: 0.1,
        });
        const plane = new THREE.Mesh(geometren, material2);
        plane.receiveShadow = true;
        scene.add(plane);
        
        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);
        const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
        directionalLight.position.set(5, 10, 7);
        directionalLight.castShadow = true;
        directionalLight.shadow.mapSize.width = 2048;
        directionalLight.shadow.mapSize.height = 2048;
        directionalLight.shadow.camera.near = 0.5;
        directionalLight.shadow.camera.far = 100;
        scene.add(directionalLight);

        // GUI controls
        const gui = new GUI();
        const waveSettings = {
            amplitude: 10.0,
            frequency: 10.0,
            speed: 2,
            color: 0x00aaff,
        };

        gui.add(waveSettings, 'amplitude', 0.1, 100.0);
        gui.add(waveSettings, 'frequency', 0.5, 50.0);
        gui.add(waveSettings, 'speed', 0.1, 30.0);
        gui.addColor(waveSettings, 'color').onChange(value => material2.color.set(value));
        
        // Wave Animation
        function animateWave(time) {
            const positions = geometren.attributes.position.array;
            const uTime = time * 0.001 * waveSettings.speed;
            
            for (let i = 0; i < positions.length; i += 3) {
                const z = positions[i + 2];
                positions[i + 1] = Math.sin(z * waveSettings.frequency + uTime) * waveSettings.amplitude;
            }
            
            geometren.attributes.position.needsUpdate = true;
            geometren.computeVertexNormals();
            renderer.render(scene, camera);
            requestAnimationFrame(animateWave);
        }
        
        animateWave();
        
        const handleResize = () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        };
        window.addEventListener('resize', handleResize);
        
        // Cleanup
        return () => {
            window.removeEventListener('resize', handleResize);
            controls.dispose();
            gui.destroy();
            renderer.dispose();
            if (mountNode) mountNode.removeChild(renderer.domElement);
            
            scene.traverse((object) => {
                if (object.geometry) object.geometry.dispose();
                if (object.material) object.material.dispose();
            });
            scene.clear();
        };
    }, []);

    return <div ref={mountRef}></div>;
}

export default Planner;
