import React, { useRef, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import SubmitForm from './submitForm';
import './Planner.css';

function Planner() {
    const [isOpen, setIsOpen] = useState(false);
    const togglePopup = () => {
        setIsOpen(!isOpen);
    };
    return (
        <div className="planner">
            <section>
                <h2>Welcome to Planner</h2>
                <h3>Where you can write your path down for Success</h3>
            </section>
            <section>
                <h2>Planner Table</h2>
                <div className="goalenterer">
                    <button className="planner-button" onClick={togglePopup}>Enter Goal</button>
                    <Link to="/activitysetter" className="planner-link-button">Set Activities</Link>
                    {isOpen && (
                        <div className="popup-overlay">
                            <div className="popup-content">
                                <form id="myform">
                                    <label htmlFor="goal">What is your goal?</label>
                                    <input type="text" id="goal" name="goal" /><br></br>
                                    <label htmlFor="priority">What Priority is your Goal?</label>
                                    <input type="text" id="Priority" name="priority" /><br></br>
                                    <input type="submit" id="gsubmit" className="planner-button" />
                                </form>
                                <button className="planner-button" onClick={togglePopup}>Close</button>
                            </div>
                        </div>
                    )}
                </div>
                <table className="use" cellPadding="5">
                    <tr>
                        <th scope="col">Priority</th>
                        <th scope="col">Goals</th>
                        <th scope="col">Action</th>
                        <th scope="col">Start Time</th>
                        <th scope="col">End Time</th>
                    </tr>
                    <tr>
                        <td>{}</td>
                    </tr>
                </table>
            </section>
        </div>
    );
}

export default Planner;

// I added class names to style the buttons and linked a 'Planner.css' file! I’ll draft the styling next. ✨
