import React, { useState } from 'react';
import submitForm from './submitForm';  // Import updated submitForm

function Planner() {
    const [isOpen, setIsOpen] = useState(false);
    const [response, setResponse] = useState(null);

    const togglePopup = () => {
        setIsOpen(!isOpen);
    };

    const handleFormSubmit = (e) => {
        // Create a callback function to update state in Planner
        submitForm(e, (resp) => {
            setResponse(resp);  // Handle the response (e.g., display it or update state)
        });
    };

    return (
        <div className="planner">
            <section>
                <h2>Welcome to Planner</h2>
                <h3>Where you can write your path down for Success</h3>
            </section>
            <section>
                <h2>Planner Table</h2>
                <div className="goalenterer">
                    <button onClick={togglePopup}>Enter Goal</button>
                    {isOpen && (
                        <div className="popup-overlay">
                            <div className="popup-content">
                                <form id="myform" onSubmit={handleFormSubmit}>
                                    <label htmlFor="goal">What is your goal?</label>
                                    <input type="text" id="goal" name="goal" required /><br />
                                    <label htmlFor="priority">What Priority is your Goal?</label>
                                    <input type="text" id="priority" name="priority" required /><br />
                                    <input type="submit" value="Submit" />
                                </form>
                                <button onClick={togglePopup}>Close</button>
                            </div>
                        </div>
                    )}
                </div>
                <table className="use" cellPadding="5">
                    <thead>
                        <tr>
                            <th scope="col">Priority</th>
                            <th scope="col">Goals</th>
                            <th scope="col">Action</th>
                            <th scope="col">Start Time</th>
                            <th scope="col">End Time</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>{response ? response.priority : 'N/A'}</td>
                            <td>{response ? response.goal : 'N/A'}</td>
                            <td>{/* Add action button here */}</td>
                            <td>{/* Add start time here */}</td>
                            <td>{/* Add end time here */}</td>
                        </tr>
                    </tbody>
                </table>
            </section>
        </div>
    );
}

export default Planner;
