import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './activitysetter.css';

function ActivitySetter() {
    const [step, setStep] = useState(1);
    const [activity, setActivity] = useState('');
    const [duration, setDuration] = useState('');
    const [notes, setNotes] = useState('');

    const handleNext = () => setStep((prev) => prev + 1);
    const handleBack = () => setStep((prev) => prev - 1);

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("New Activity:", { activity, duration, notes });
        setActivity('');
        setDuration('');
        setNotes('');
        setStep(1);
    };

    return (
        <div className="activity-setter-page">
            <nav className="activity-nav">
                <Link to="/" className="activity-link">Home</Link>
                <Link to="/planner" className="activity-link">Planner</Link>
            </nav>
            <div className="activity-setter">
                <h2 className="activity-title">Set Activities for Your Goal</h2>
                <form onSubmit={handleSubmit} className="activity-form">
                    {step === 1 && (
                        <div>
                            <label htmlFor="activity" className="activity-label">What is your activity?</label>
                            <input 
                                type="text" 
                                id="activity" 
                                value={activity} 
                                onChange={(e) => setActivity(e.target.value)} 
                                placeholder="Enter an activity..."
                                className="activity-input"
                            />
                            <button type="button" onClick={handleNext} className="activity-button">Next</button>
                        </div>
                    )}
                    {step === 2 && (
                        <div>
                            <label htmlFor="duration" className="activity-label">How long will it take? (in minutes)</label>
                            <input 
                                type="number" 
                                id="duration" 
                                value={duration} 
                                onChange={(e) => setDuration(e.target.value)} 
                                placeholder="Enter duration..."
                                className="activity-input"
                            />
                            <button type="button" onClick={handleBack} className="activity-button">Back</button>
                            <button type="button" onClick={handleNext} className="activity-button">Next</button>
                        </div>
                    )}
                    {step === 3 && (
                        <div>
                            <label htmlFor="notes" className="activity-label">Any notes?</label>
                            <textarea 
                                id="notes" 
                                value={notes} 
                                onChange={(e) => setNotes(e.target.value)} 
                                placeholder="Additional details..."
                                className="activity-input"
                            />
                            <button type="button" onClick={handleBack} className="activity-button">Back</button>
                            <button type="submit" className="activity-button">Submit</button>
                        </div>
                    )}
                </form>
            </div>
        </div>
    );
}

export default ActivitySetter;
