import React, { useState } from 'react';
import './Planner.css';
import ActivitySetter from './ActivitySetter'

function Planner() {
  const [goal, setGoal] = useState(''); // Store the user's goal
  const [enteredGoal, setEnteredGoal] = useState(null); // Store the submitted goal
  const [activities, setActivities] = useState([]); // Store list of activities

  const handleGoalSubmission = (newGoal) => {
    setGoal(newGoal);  // Update the goal state when entered
    setEnteredGoal(newGoal); // Save entered goal
  };

  const addActivity = (activity) => {
    setActivities([...activities, activity]); // Add activity to the list
  };

  const deleteActivity = (index) => {
    const updatedActivities = activities.filter((_, i) => i !== index); // Remove the activity by index
    setActivities(updatedActivities);
  };

  return (
    <div className="planner-container">
      <h1 className="planner-title">Planner</h1>

      {/* Goal Setting Section */}
      <section className="goal-section">
        <h2>Enter Your Goal</h2>
        <ActivitySetter onGoalSubmit={handleGoalSubmission} />
        <h3>Your Current Goal: {enteredGoal || 'No goal set'}</h3>
      </section>

      {/* Full Planner Table (Activities, Settings, etc.) */}
      <section className="planner-table-section">
        <h2>Activity Table</h2>

        {/* Activity Table */}
        <table className="activity-table">
          <thead>
            <tr>
              <th>Activity</th>
              <th>Status</th>
              <th>Priority</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {activities.map((activity, index) => (
              <tr key={index}>
                <td>{activity.name}</td>
                <td>{activity.status}</td>
                <td>{activity.priority}</td>
                <td>
                  <button className="edit-button">Edit</button>
                  <button className="delete-button" onClick={() => deleteActivity(index)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Button to add activities */}
        <button className="add-activity-button" onClick={() => addActivity({ 
          name: 'New Activity', 
          status: 'Incomplete', 
          priority: activities.length + 1 // Numbered priority (1, 2, 3, ...)
        })}>
          Add Activity
        </button>
      </section>
    </div>
  );
}

export default Planner;
