import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function ActivitySetter({ onAddActivity }) {
    const [activity, setActivity] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (activity.trim()) {
            onAddActivity(activity);
            setActivity('');
        }
    };

    return (
        <div className="activity-setter-page">
            <nav>
                <Link to="/">Home</Link>
                <Link to="/planner">Planner</Link>
                <Link to="/activity-setter">Activity Setter</Link>
            </nav>
            <div className="activity-setter">
                <h2>Set Activities for Your Goal</h2>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="activity">Activity:</label>
                    <input 
                        type="text" 
                        id="activity" 
                        value={activity} 
                        onChange={(e) => setActivity(e.target.value)} 
                        placeholder="Enter an activity..."
                    />
                    <button type="submit">Add Activity</button>
                </form>
            </div>
        </div>
    );
}

export default ActivitySetter;

// Now you can add a route for this page in your App.js like:
