import React, { useState } from 'react';

function Planner() {
    const [isOpen, setIsOpen] = useState(false);
    const [goals, setGoals] = useState([]); // Store goals in state

    const togglePopup = () => {
        setIsOpen(!isOpen);
    };

    const handleFormSubmit = (e) => {
        e.preventDefault(); // Prevent page reload

        // Get form data
        const formData = new FormData(e.target);
        const newGoal = {
            goal: formData.get('goal'),
            priority: formData.get('priority'),
        };

        // Add the new goal to the state
        setGoals((prevGoals) => [...prevGoals, newGoal]);

        // Close the popup and reset the form
        togglePopup();
        e.target.reset();
    };

    return (
        <div className="planner">
            <section>
                <h2>Welcome to Planner</h2>
                <h3>Where you can write your path down for Success</h3>
            </section>
            <section>
                <h2>Planner Table</h2>
                <div className="goalenterer">
                    <button onClick={togglePopup}>Enter Goal</button>
                    {isOpen && (
                        <div className="popup-overlay">
                            <div className="popup-content">
                                <form id="myform" onSubmit={handleFormSubmit}>
                                    <label htmlFor="goal">What is your goal?</label>
                                    <input type="text" id="goal" name="goal" required /><br />
                                    <label htmlFor="priority">What Priority is your Goal?</label>
                                    <input type="text" id="priority" name="priority" required /><br />
                                    <input type="submit" value="Submit" />
                                </form>
                                <button onClick={togglePopup}>Close</button>
                            </div>
                        </div>
                    )}
                </div>
                <table className="use" cellPadding="5">
                    <thead>
                        <tr>
                            <th scope="col">Priority</th>
                            <th scope="col">Goals</th>
                            <th scope="col">Action</th>
                            <th scope="col">Start Time</th>
                            <th scope="col">End Time</th>
                        </tr>
                    </thead>
                    <tbody>
                        {goals.map((goal, index) => (
                            <tr key={index}>
                                <td>{goal.priority}</td>
                                <td>{goal.goal}</td>
                                <td></td> {/* Empty Action */}
                                <td></td> {/* Empty Start Time */}
                                <td></td> {/* Empty End Time */}
                            </tr>
                        ))}
                    </tbody>
                </table>
            </section>
        </div>
    );
}

export default Planner;
