import React, { useState } from 'react';
import { Link, useOutletContext } from 'react-router-dom';
import './Planner.css';

function Planner() {
    const { activities } = useOutletContext();
    const [isOpen, setIsOpen] = useState(false);
    const [goal, setGoal] = useState('');
    const [priority, setPriority] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [schedule, setSchedule] = useState({});
    const [status, setStatus] = useState('Not Started');
    const [goalsList, setGoalsList] = useState([]);

    const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

    const togglePopup = () => setIsOpen(!isOpen);

    const handleScheduleChange = (day, field, value) => {
        setSchedule((prev) => ({
            ...prev,
            [day]: { ...prev[day], [field]: value },
        }));
    };

    const addBreakTime = (day) => {
        setSchedule((prev) => {
            const breaks = prev[day]?.breaks || [];
            return {
                ...prev,
                [day]: {
                    ...prev[day],
                    breaks: [...breaks, { start: '', end: '' }],
                },
            };
        });
    };

    const updateBreakTime = (day, index, field, value) => {
        setSchedule((prev) => {
            const breaks = prev[day]?.breaks.map((breakTime, i) => (
                i === index ? { ...breakTime, [field]: value } : breakTime
            ));
            return {
                ...prev,
                [day]: { ...prev[day], breaks },
            };
        });
    };

    const removeBreakTime = (day, index) => {
        setSchedule((prev) => {
            const breaks = prev[day]?.breaks.filter((_, i) => i !== index);
            return {
                ...prev,
                [day]: { ...prev[day], breaks },
            };
        });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        if (goal && priority && startDate && endDate && status && Object.keys(schedule).length) {
            const newGoal = {
                goal,
                priority: parseInt(priority, 10),
                startDate,
                endDate,
                schedule,
                status,
            };
            setGoalsList([...goalsList, newGoal]);
            setGoal('');
            setPriority('');
            setStartDate('');
            setEndDate('');
            setSchedule({});
            setStatus('Not Started');
            setIsOpen(false);
        }
    };

    return (
        <div className="planner">
            <section>
                <h2>Welcome to Planner</h2>
                <h3>Where you can write your path down for Success</h3>
            </section>
            <section>
                <h2>Planner Table</h2>
                <div className="goalenterer">
                    <button onClick={togglePopup}>Enter Goal</button>
                    {isOpen && (
                        <div className="popup-overlay">
                            <div className="popup-content">
                                <form id="myform" onSubmit={handleSubmit} className="form-grid">
                                    <div>
                                        <label htmlFor="goal">What is your goal?</label>
                                        <input
                                            type="text"
                                            id="goal"
                                            value={goal}
                                            onChange={(e) => setGoal(e.target.value)}
                                        />
                                        <Link to="/activitysetter" className="activity-link">Go to Activity Setter</Link>
                                    </div>
                                    <div>
                                        <label htmlFor="priority">Priority (Number):</label>
                                        <input
                                            type="number"
                                            id="priority"
                                            value={priority}
                                            onChange={(e) => setPriority(e.target.value)}
                                        />
                                    </div>
                                    <div>
                                        <label htmlFor="start-date">Start Date:</label>
                                        <input
                                            type="date"
                                            id="start-date"
                                            value={startDate}
                                            onChange={(e) => setStartDate(e.target.value)}
                                        />
                                    </div>
                                    <div>
                                        <label htmlFor="end-date">End Date:</label>
                                        <input
                                            type="date"
                                            id="end-date"
                                            value={endDate}
                                            onChange={(e) => setEndDate(e.target.value)}
                                        />
                                    </div>
                                    <div className="schedule-section">
                                        <h3>Work Schedule:</h3>
                                        {daysOfWeek.map((day) => (
                                            <div key={day} className="day-schedule">
                                                <label>
                                                    <input
                                                        type="checkbox"
                                                        checked={!!schedule[day]?.active}
                                                        onChange={(e) => handleScheduleChange(day, 'active', e.target.checked)}
                                                    />
                                                    {day}
                                                </label>
                                                {schedule[day]?.active && (
                                                    <div className="time-inputs">
                                                        <input
                                                            type="time"
                                                            placeholder="Start Time"
                                                            value={schedule[day]?.startTime || ''}
                                                            onChange={(e) => handleScheduleChange(day, 'startTime', e.target.value)}
                                                        />
                                                        <input
                                                            type="time"
                                                            placeholder="End Time"
                                                            value={schedule[day]?.endTime || ''}
                                                            onChange={(e) => handleScheduleChange(day, 'endTime', e.target.value)}
                                                        />
                                                        <div>
                                                            <button type="button" onClick={() => addBreakTime(day)}>
                                                                + Add Break
                                                            </button>
                                                        </div>
                                                        {schedule[day]?.breaks?.map((breakTime, index) => (
                                                            <div key={index}>
                                                                <input
                                                                    type="time"
                                                                    placeholder="Break Start"
                                                                    value={breakTime.start}
                                                                    onChange={(e) => updateBreakTime(day, index, 'start', e.target.value)}
                                                                />
                                                                <input
                                                                    type="time"
                                                                    placeholder="Break End"
                                                                    value={breakTime.end}
                                                                    onChange={(e) => updateBreakTime(day, index, 'end', e.target.value)}
                                                                />
                                                                <button type="button" onClick={() => removeBreakTime(day, index)}>
                                                                    Remove Break
                                                                </button>
                                                            </div>
                                                        ))}
                                                    </div>
                                                )}
                                            </div>
                                        ))}
                                    </div>
                                    <div className="form-actions">
                                        <input type="submit" id="gsubmit" value="Submit" />
                                        <button onClick={togglePopup}>Close</button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    )}
                </div>
                <table className="use" cellPadding="5">
                    <thead>
                        <tr>
                            <th>Priority</th>
                            <th>Goal</th>
                            <th>Start Date</th>
                            <th>End Date</th>
                            <th>Status</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {goalsList.map((goalItem, index) => (
                            <tr key={index}>
                                <td>{goalItem.priority}</td>
                                <td>{goalItem.goal}</td>
                                <td>{goalItem.startDate}</td>
                                <td>{goalItem.endDate}</td>
                                <td>{goalItem.status}</td>
                                <td>
                                    <Link to="/activitysetter" className="action-link">Set Activity</Link>
                                    <ul>
                                        {activities.map((activity, activityIndex) => (
                                            <li key={activityIndex}>{activity}</li>
                                        ))}
                                    </ul>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </section>
        </div>
    );
}

export default Planner;
