import React, { useState } from 'react';

function Planner() {
    const [isOpen, setIsOpen] = useState(false);
    const [goal, setGoal] = useState('');
    const [priority, setPriority] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [schedule, setSchedule] = useState({});
    const [status, setStatus] = useState('Not Started');
    const [goalsList, setGoalsList] = useState([]);

    const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

    const togglePopup = () => setIsOpen(!isOpen);

    const handleScheduleChange = (day, field, value) => {
        setSchedule((prev) => ({
            ...prev,
            [day]: { ...prev[day], [field]: value },
        }));
    };

    const addBreak = (day) => {
        setSchedule((prev) => ({
            ...prev,
            [day]: {
                ...prev[day],
                breaks: [...(prev[day]?.breaks || []), { start: '', end: '' }],
            },
        }));
    };

    const handleBreakChange = (day, index, field, value) => {
        const updatedBreaks = [...(schedule[day]?.breaks || [])];
        updatedBreaks[index][field] = value;
        setSchedule((prev) => ({
            ...prev,
            [day]: { ...prev[day], breaks: updatedBreaks },
        }));
    };

    const handleTickMarkClick = (day) => {
        addBreak(day);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        if (goal && priority && startDate && endDate && status && Object.keys(schedule).length) {
            const newGoal = {
                goal,
                priority: parseInt(priority, 10),
                startDate,
                endDate,
                schedule,
                status,
            };
            setGoalsList([...goalsList, newGoal]);
            setGoal('');
            setPriority('');
            setStartDate('');
            setEndDate('');
            setSchedule({});
            setStatus('Not Started');
            setIsOpen(false);
        }
    };

    return (
        <div className="planner">
            <section>
                <h2>Welcome to Planner</h2>
                <h3>Where you can write your path down for Success</h3>
            </section>

            {/* Goal Entry Section */}
            <section>
                <h2>Planner Table</h2>
                <div className="goalenterer">
                    <button onClick={togglePopup}>Enter Goal</button>
                    {isOpen && (
                        <div className="popup-overlay">
                            <div className="popup-content">
                                <form id="myform" onSubmit={handleSubmit} className="form-grid">
                                    <div>
                                        <label htmlFor="goal">What is your goal?</label>
                                        <input
                                            type="text"
                                            id="goal"
                                            value={goal}
                                            onChange={(e) => setGoal(e.target.value)}
                                        />
                                    </div>
                                    <div>
                                        <label htmlFor="priority">Priority (Number):</label>
                                        <input
                                            type="number"
                                            id="priority"
                                            value={priority}
                                            onChange={(e) => setPriority(e.target.value)}
                                        />
                                    </div>
                                    <div>
                                        <label htmlFor="start-date">Start Date:</label>
                                        <input
                                            type="date"
                                            id="start-date"
                                            value={startDate}
                                            onChange={(e) => setStartDate(e.target.value)}
                                        />
                                    </div>
                                    <div>
                                        <label htmlFor="end-date">End Date:</label>
                                        <input
                                            type="date"
                                            id="end-date"
                                            value={endDate}
                                            onChange={(e) => setEndDate(e.target.value)}
                                        />
                                    </div>
                                    <div className="schedule-section">
                                        <h3>Work Schedule:</h3>
                                        {daysOfWeek.map((day) => (
                                            <div key={day} className="day-schedule">
                                                <label>
                                                    <input
                                                        type="checkbox"
                                                        checked={!!schedule[day]?.active}
                                                        onChange={(e) => handleScheduleChange(day, 'active', e.target.checked)}
                                                    />
                                                    {day}
                                                </label>
                                                {schedule[day]?.active && (
                                                    <div className="time-inputs">
                                                        <input
                                                            type="time"
                                                            placeholder="Start Time"
                                                            value={schedule[day]?.startTime || ''}
                                                            onChange={(e) => handleScheduleChange(day, 'startTime', e.target.value)}
                                                        />
                                                        <input
                                                            type="time"
                                                            placeholder="End Time"
                                                            value={schedule[day]?.endTime || ''}
                                                            onChange={(e) => handleScheduleChange(day, 'endTime', e.target.value)}
                                                        />
                                                        <div>
                                                            <label>
                                                                <input
                                                                    type="checkbox"
                                                                    checked={!!schedule[day]?.hasBreak}
                                                                    onChange={(e) => handleScheduleChange(day, 'hasBreak', e.target.checked)}
                                                                />
                                                                Add Break Times
                                                            </label>
                                                            {schedule[day]?.hasBreak && (
                                                                <div>
                                                                    {schedule[day]?.breaks?.map((breakTime, index) => (
                                                                        <div key={index} className="break-time">
                                                                            <input
                                                                                type="time"
                                                                                placeholder="Break Start"
                                                                                value={breakTime.start}
                                                                                onChange={(e) => handleBreakChange(day, index, 'start', e.target.value)}
                                                                            />
                                                                            <input
                                                                                type="time"
                                                                                placeholder="Break End"
                                                                                value={breakTime.end}
                                                                                onChange={(e) => handleBreakChange(day, index, 'end', e.target.value)}
                                                                            />
                                                                        </div>
                                                                    ))}
                                                                    <button type="button" onClick={() => handleTickMarkClick(day)}>
                                                                        ✔ Add Break
                                                                    </button>
                                                                </div>
                                                            )}
                                                        </div>
                                                    </div>
                                                )}
                                            </div>
                                        ))}
                                    </div>
                                    <div>
                                        <label htmlFor="status">Status:</label>
                                        <select
                                            id="status"
                                            value={status}
                                            onChange={(e) => setStatus(e.target.value)}
                                        >
                                            <option value="Not Started">Not Started</option>
                                            <option value="In Progress">In Progress</option>
                                            <option value="Completed">Completed</option>
                                            <option value="Paused">Paused</option>
                                        </select>
                                    </div>
                                    <div className="form-actions">
                                        <input type="submit" id="gsubmit" value="Submit" />
                                        <button onClick={togglePopup}>Close</button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    )}
                </div>
            </section>

            {/* Goals List Section (Table) */}
            <section>
                <h2>Goals List</h2>
                {goalsList.length > 0 && (
                    <table>
                        <thead>
                            <tr>
                                <th>Goal</th>
                                <th>Priority</th>
                                <th>Start Date</th>
                                <th>End Date</th>
                                <th>Status</th>
                                <th>Schedule</th>
                            </tr>
                        </thead>
                        <tbody>
                            {goalsList.map((goalItem, index) => (
                                <tr key={index}>
                                    <td>{goalItem.goal}</td>
                                    <td>{goalItem.priority}</td>
                                    <td>{goalItem.startDate}</td>
                                    <td>{goalItem.endDate}</td>
                                    <td>{goalItem.status}</td>
                                    <td>
                                        {Object.keys(goalItem.schedule).map((day) => (
                                            <div key={day}>
                                                <strong>{day}: </strong>
                                                {goalItem.schedule[day]?.active && (
                                                    <>
                                                        {goalItem.schedule[day]?.startTime} - {goalItem.schedule[day]?.endTime}
                                                        {goalItem.schedule[day]?.breaks?.map((breakTime, idx) => (
                                                            <div key={idx}>
                                                                Break: {breakTime.start} - {breakTime.end}
                                                            </div>
                                                        ))}
                                                    </>
                                                )}
                                            </div>
                                        ))}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
                {goalsList.length === 0 && <p>No goals added yet.</p>}
            </section>
        </div>
    );
}

export default Planner;
